import { useEffect, useState } from 'react';
import Styles from './TODO.module.css';
import { dummy } from './dummy';
import axios from 'axios';

export function TODO(props) {
    const [newTodo, setNewTodo] = useState('');
    const [todoData, setTodoData] = useState(dummy);
    const [loading, setLoading] = useState(true);
    const [editId, setEditId] = useState(null);
    const [editTitle, setEditTitle] = useState('');
    const [editDescription, setEditDescription] = useState('');

    useEffect(() => {
        const fetchTodo = async () => {
            try {
                const apiData = await getTodo();
                setTodoData(apiData);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching todos:', error);
                setLoading(false);
            }
        };
        fetchTodo();
    }, []);

    const getTodo = async () => {
        const options = {
            method: 'GET',
            url:`http://localhost:8000/api/todo`,
            headers: {
                accept: 'application/json',
            },
        };
        try {
            const response = await axios.request(options);
            console.log('API Response:', response.data); // Log the response data
            return response.data;
        } catch (err) {
            console.log('Error fetching todos:', err);
            return [];
        }
    };

    const addTodo = async () => {
        const options = {
            method: 'POST',
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: 'application/json',
            },
            data: {
                title: newTodo,
                description: '',
            },
        };
        try {
            const response = await axios.request(options);
            setTodoData((prevData) => [...prevData, response.data]);
            setNewTodo('');
        } catch (err) {
            console.log(err);
        }
    };

    const deleteTodo = async (id) => {
        const options = {
            method: 'DELETE',
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: 'application/json',
            },
        };
        try {
            await axios.request(options);
            setTodoData((prevData) => prevData.filter((todo) => todo._id !== id));
        } catch (err) {
            console.log(err);
        }
    };

    const updateTodo = async (id) => {
        const todoToUpdate = todoData.find((todo) => todo._id === id);
        const options = {
            method: 'PATCH',
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: 'application/json',
            },
            data: {
                done: !todoToUpdate.done,
            },
        };
        try {
            const response = await axios.request(options);
            setTodoData((prevData) => prevData.map((todo) => (todo._id === id ? response.data : todo)));
        } catch (err) {
            console.log(err);
        }
    };

    const editTodo = (id) => {
        setEditId(id);
        const todoToEdit = todoData.find((todo) => todo._id === id);
        setEditTitle(todoToEdit.title);
        setEditDescription(todoToEdit.description || '');
    };

    const saveTodo = async (id) => {
        const options = {
            method: 'PUT',
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: 'application/json',
            },
            data: {
                title: editTitle,
                description: editDescription,
            },
        };
        try {
            const response = await axios.request(options);
            setTodoData((prevData) => prevData.map((todo) => (todo._id === id ? response.data : todo)));
            setEditId(null);
            setEditTitle('');
            setEditDescription('');
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>Tasks</h1>
                <span>
                    <input
                        className={Styles.todoInput}
                        type='text'
                        name='New Todo'
                        value={newTodo}
                        onChange={(event) => {
                            setNewTodo(event.target.value);
                        }}
                    />
                    <button
                        id='addButton'
                        name='add'
                        className={Styles.addButton}
                        onClick={addTodo}
                    >
                        + New Todo
                    </button>
                </span>
            </div>
            <div id='todoContainer' className={Styles.todoContainer}>
                {loading ? (
                    <p style={{ color: 'white' }}>Loading...</p>
                ) : todoData.length > 0 ? (
                    todoData.map((entry) => (
                        <div key={entry._id} className={Styles.todo}>
                            {editId === entry._id ? (
                                <span className={Styles.infoContainer}>
                                    <input
                                        type='text'
                                        value={editTitle}
                                        onChange={(event) => setEditTitle(event.target.value)}
                                    />
                                    <textarea
                                        value={editDescription}
                                        onChange={(event) => setEditDescription(event.target.value)}
                                    />
                                    <button onClick={() => saveTodo(entry._id)}>Save</button>
                                </span>
                            ) : (
                                <span className={Styles.infoContainer}>
                                    <input
                                        type='checkbox'
                                        checked={entry.done}
                                        onChange={() => updateTodo(entry._id)}
                                    />
                                    {entry.title}
                                    <p>{entry.description || ''}</p>
                                </span>
                            )}
                            <span
                                style={{ cursor: 'pointer' }}
                                onClick={() => deleteTodo(entry._id)}
                            >
                                Delete
                            </span>
                            <span
                                style={{ cursor: 'pointer' }}
                                onClick={() => editTodo(entry._id)}
                            >
                                Edit
                            </span>
                        </div>
                    ))
                ) : (
                    <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
                )}
            </div>
        </div>
    );
}
